        $this->curlOptions = array_merge($this->curlOptions, $curlOptions);
    }

    public function setLogger(object $logger): void {
        $this->logger = $logger;
    }

    public function getResponseBody(): string {
        return $this->responseBody;
    }

    public function getResponseHeader(): array {
        return $this->responseHeader;
    }

    public function getResponseHttpCode(): int {
        return $this->responseHttpCode;
    }

    public function getResponseClientError(): string|null {
        return $this->responseClientError;
    }

    public function getResponseClientInfo(): array {
        return $this->responseClientInfo;
    }

    public function fetchResponseHeader(resource $curl, string $header): int {
        // Callback function for processing response headers
        return curl_getinfo($curl, CURLINFO_HTTP_CODE);
    }

    public function prepareRequestHeaders(): array {
        return array_merge($this->requestHeader, $this->requestArguments['headers'] ?? []);
    }

    private function prepareRequest(): void {
        $this->curlOptions = array_merge([
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_TIMEOUT => 30
        ], $this->curlOptions);
        $this->curlOptions[CURLOPT_URL] = $this->requestArguments['uri'];
        $this->curlOptions[CURLOPT_CUSTOMREQUEST] = $this->requestArguments['method'];
        $this->curlOptions[CURLOPT_HTTPHEADER] = $this->prepareRequestHeaders();
        if ($this->requestArguments['multipart']) {
            $multipartData = [];
            foreach ($this->requestArguments['parameters'] as $key => $value) {
                $multipartData[] = sprintf("%s=%s", urlencode($key), urlencode($value));
            }
            $this->curlOptions[CURLOPT_POSTFIELDS] = implode('&', $multipartData);
        } else {
            $this->curlOptions[CURLOPT_POSTFIELDS] = http_build_query($this->requestArguments['parameters']);
        }
    }

    private function executeRequest(): void {
        $curl = curl_init();
        curl_setopt_array($curl, $this->curlOptions);
        curl_setopt($curl, CURLOPT_WRITEFUNCTION, function ($curl, $data) {
            $this->responseBody .= $data;
            return strlen($data);
        });
        curl_setopt($curl, CURLOPT_HEADERFUNCTION, function ($curl, $header) {
            $this->responseHeader[] = $header;
            return strlen($header);
        });
        $this->responseClientInfo = curl_getinfo($curl);
        $this->responseHttpCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);
        $this->responseClientError = curl_error($curl);
        curl_exec($curl);
        curl_close($curl);
        if ($this->logger) {
            $this->logger->logRequest($this->requestArguments, $this->responseHeader, $this->responseHttpCode, $this->responseBody);
        }
    }

    public function getRequestArguments(): array {
        return $this->requestArguments;
    }
