        $this->curl = curl_init();
        curl_setopt_array($this->curl, $this->curlOptions);
    }

    private function executeRequest() {
        if ($this->requestArguments['method'] === 'GET') {
            curl_setopt($this->curl, CURLOPT_HTTPGET, true);
        } elseif ($this->requestArguments['multipart']) {
            curl_setopt($this->curl, CURLOPT_POSTFIELDS, $this->requestArguments['parameters']);
        } else {
            curl_setopt($this->curl, CURLOPT_CUSTOMREQUEST, $this->requestArguments['method']);
            curl_setopt($this->curl, CURLOPT_POSTFIELDS, http_build_query($this->requestArguments['parameters']));
        }

        $this->responseBody = curl_exec($this->curl);
        $this->responseHeader = $this->fetchResponseHeader();
        $this->responseHttpCode = curl_getinfo($this->curl, CURLINFO_HTTP_CODE);
        $this->responseClientError = curl_error($this->curl);
        $this->responseClientInfo = curl_getinfo($this->curl);

        if ($this->logger) {
            $this->logger->logRequest($this->requestArguments);
            $this->logger->logResponse($this->responseHeader, $this->responseHttpCode, $this->responseBody);
        }
    }

    private function fetchResponseHeader() {
        $header_size = curl_getinfo($this->curl, CURLINFO_HEADER_SIZE);
        $header = substr($this->responseBody, 0, $header_size);
        $header_lines = explode("\r\n", substr($header, 0, -4));
        $headers = [];
        foreach ($header_lines as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[strtolower(trim($key))] = trim($value);
            }
        }
        return $headers;
    }

    private function prepareRequestHeaders() {
        $headers = [];
        foreach ($this->requestArguments['headers'] as $key => $value) {
            $headers[] = $key . ': ' . $value;
        }
        return $headers;
    }
