        return self::sendRequest(self::createRequest($url, 'POST', self::createMultipartBody($fields, $name, $fileName, $fileBody, $mimeType), $headers));
    }

    private static function sendRequest($request) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $request->url);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $request->method);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $request->headers);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        $response = curl_exec($ch);
        $statusCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $error = curl_error($ch);
        curl_close($ch);
        return new Response($response, self::parseHeaders(substr($response, 0, strpos($response, "\r\n\r\n"))), $statusCode, $error);
    }

    private static function createRequest($url, $method, $body, $headers) {
        return (object)['url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers];
    }

    private static function createMultipartBody($fields, $name, $fileName, $fileBody, $mimeType) {
        $boundary = uniqid();
        $multipartBody = "";
        foreach ($fields as $key => $value) {
            $multipartBody .= "--$boundary\r\n";
            $multipartBody .= "Content-Disposition: form-data; name=\"" . self::escapeQuotes($key) . "\"\r\n\r\n";
            $multipartBody .= $value . "\r\n";
        }
        $multipartBody .= "--$boundary\r\n";
        $multipartBody .= "Content-Disposition: form-data; name=\"" . self::escapeQuotes($name) . "\"; filename=\"" . self::escapeQuotes($fileName) . "\"\r\n";
        $multipartBody .= "Content-Type: " . ($mimeType ? $mimeType : "application/octet-stream") . "\r\n\r\n";
        $multipartBody .= $fileBody . "\r\n";
        $multipartBody .= "--$boundary--\r\n";
        return $multipartBody;
    }

    public static function userAgent() {
        return "SDK/" . self::ucwordsHyphen("PHP") . "/" . PHP_VERSION;
    }

    public static function parseHeaders($raw) {
        $headers = [];
        foreach (explode("\r\n", $raw) as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $key = self::ucwordsHyphen(trim($key));
                $headers[$key] = trim($value);
            }
        }
        return $headers;
    }

    public static function escapeQuotes($str) {
        return str_replace('"', '\"', $str);
    }

    public static function ucwordsHyphen($str) {
        return str_replace(' ', '-', ucwords(str_replace('-', ' ', $str)));
    }
}
