function make_curl_request($postData, $OPENAI_API_KEY) {
    $responsedata = "";
    $ch = curl_init();

    $headers = [
        'Accept: application/json',
        'Content-Type: application/json',
        'Authorization: Bearer ' . $OPENAI_API_KEY
    ];

    setcookie("errcode", "");
    setcookie("errmsg", "");

    $callback = function ($ch, $data) {
        global $responsedata;
        $complete = json_decode($data);
        if (isset($complete->error)) {
            setcookie("errcode", $complete->error->code);
            setcookie("errmsg", $data);
            if (strpos($complete->error->message, "Rate limit reached") === 0) {
                setcookie("errcode", "rate_limit_reached");
            }
            if (strpos($complete->error->message, "Your access was terminated") === 0) {
                setcookie("errcode", "access_terminated");
            }
            if (strpos($complete->error->message, "You didn't provide an API key") === 0) {
                setcookie("errcode", "no_api_key");
            }
            if (strpos($complete->error->message, "You exceeded your current quota") === 0) {
                setcookie("errcode", "insufficient_quota");
            }
            if (strpos($complete->error->message, "That model is currently overloaded") === 0) {
                setcookie("errcode", "model_overloaded");
            }
            $responsedata = $data;
        } else {
            echo $data;
            $responsedata .= $data;
            flush();
        }
        return strlen($data);
    };

    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, FALSE);
    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, FALSE);
    curl_setopt($ch, CURLOPT_URL, 'https://api.openai.com/v1/chat/completions');
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
    curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
    curl_setopt($ch, CURLOPT_POST, 1);
    curl_setopt($ch, CURLOPT_POSTFIELDS, $postData);
    curl_setopt($ch, CURLOPT_WRITEFUNCTION, $callback);
    curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 120);
    curl_setopt($ch, CURLOPT_MAXREDIRS, 3);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
    curl_setopt($ch, CURLOPT_AUTOREFERER, true);

    curl_exec($ch);
    curl_close($ch);

    return $responsedata;
}

