        $file = fopen($filepath, 'rb');
        $bin = fread($file, 2);
        fclose($file);
        $hex = bin2hex($bin);
        $mimeType = '';
        if (substr($hex, 0, 2) == '4D' && substr($hex, 1, 2) == '5A') {
            $mimeType = 'application/x-zip-compressed';
        } elseif (substr($hex, 0, 2) == '52' && substr($hex, 1, 2) == '46') {
            $mimeType = 'application/octet-stream';
        } elseif (substr($hex, 0, 2) == '66' && substr($hex, 1, 2) == '74') {
            $mimeType = 'image/png';
        } elseif (substr($hex, 0, 2) == '89' && substr($hex, 1, 2) == '50') {
            $mimeType = 'image/jpeg';
        } elseif (substr($hex, 0, 2) == 'FA' && substr($hex, 1, 2) == '37') {
            $mimeType = 'image/gif';
        }
