                $postData .= urlencode($key) . '=' . urlencode($value) . '&';
            }
            $postData = rtrim($postData, '&');
        }
        if (!empty($files)) {
            foreach ($files as $key => $file) {
                $postData .= '@' . $file['tmp_name'] . ';filename=' . $file['name'] . ';type=' . $file['type'] . ' ';
            }
        }
        return $postData;
    }

