            return false;
        }

        self::$raw = $response;
        self::$head = self::get_response_headers('all');
        self::$content = self::split_header_body($response)[1];
        self::$info = curl_getinfo(self::$ch);
        self::$status_code = self::$info['http_code'];
        self::$history = self::$info['redirect_count'];

