        $cookies = array();
        foreach (explode('; ', $header['Set-Cookie']) as $cookie) {
            if (strpos($cookie, $domain) !== false) {
                $cookie_data = explode('=', $cookie, 2);
                $cookies[$cookie_data[0]] = urldecode($cookie_data[1]);
            }
        }
        self::$domain_cookies[$domain] = $cookies;
    }

    public static function split_header_body($response) {
        $headers = '';
        $body = '';
        $pos = strpos($response, "\r\n\r\n");
        if ($pos !== false) {
            $headers = substr($response, 0, $pos);
            $body = substr($response, $pos + 4);
        }
        return array($headers, $body);
    }

    public static function get_history($header) {
        return self::$history;
    }

    public static function get_mimetype($filepath) {
        $file = fopen($filepath, 'rb');
        $bin = fread($file, 2);
        fclose($file);
        $hex = bin2hex($bin);
        $mimeType = '';
        if (substr($hex, 0, 2) == '4D' && substr($hex, 1, 2) == '5A') {
            $mimeType = 'application/octet-stream';
        } elseif (substr($hex, 0, 2) == '52' && substr($hex, 1, 2) == '46') {
            $mimeType = 'application/octet-stream';
        } elseif (substr($hex, 0, 2) == '66' && substr($hex, 1, 2) == '74') {
            $mimeType = 'image/png';
        } elseif (substr($hex, 0, 2) == '78' && substr($hex, 1, 2) == '9C') {
            $mimeType = 'image/jpeg';
        } elseif (substr($hex, 0, 2) == '3C' && substr($hex, 1, 2) == '3F') {
            $mimeType = 'text/html';
        } elseif (substr($hex, 0, 2) == '3C' && substr($hex, 1, 2) == '21') {
            $mimeType = 'text/html';
        }
        return $mimeType;
    }
