class Client {
    public static function get($url, $headers = []) {
        return self::sendRequest(new Request($url, 'GET', null, $headers));
    }

    public static function delete($url, $headers = []) {
        return self::sendRequest(new Request($url, 'DELETE', null, $headers));
    }

    public static function post($url, $body, $headers = []) {
        return self::sendRequest(new Request($url, 'POST', $body, $headers));
    }

    public static function put($url, $body, $headers = []) {
        return self::sendRequest(new Request($url, 'PUT', $body, $headers));
    }

    public static function multipartPost($url, $fields, $name, $fileName, $fileBody, $mimeType = null, $headers = []) {
        $multipartData = self::createMultipartData($fields, $name, $fileName, $fileBody, $mimeType);
        return self::sendRequest(new Request($url, 'POST', $multipartData, $headers));
    }

    private static function sendRequest(Request $request) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $request->url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $request->method);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
        curl_setopt($ch, CURLOPT_HTTPHEADER, self::prepareHeaders($request->headers));
        $startTime = microtime(true);
        $response = curl_exec($ch);
        $statusCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $headers = self::parseHeaders(curl_getinfo($ch, CURLINFO_HEADER_OUT));
        $endTime = microtime(true);
        curl_close($ch);
        return new Response($statusCode, $headers, $response, $endTime - $startTime);
    }

    private static function prepareHeaders($headers) {
        $userAgent = self::userAgent();
        $preparedHeaders = array_merge($headers, ['User-Agent' => $userAgent]);
        return array_map('strtolower', $preparedHeaders);
    }

    private static function createMultipartData($fields, $name, $fileName, $fileBody, $mimeType) {
        $boundary = uniqid();
        $multipartData = "";
        foreach ($fields as $key => $value) {
            $multipartData .= "--$boundary\r\n";
            $multipartData .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
            $multipartData .= $value . "\r\n";
        }
        $multipartData .= "--$boundary\r\n";
        $multipartData .= "Content-Disposition: form-data; name=\"$name\"; filename=\"$fileName\"\r\n";
        $multipartData .= "Content-Type: " . ($mimeType ? $mimeType : "application/octet-stream") . "\r\n\r\n";
        $multipartData .= $fileBody . "\r\n";
        $multipartData .= "--$boundary--\r\n";
        return $multipartData;
    }

    public static function userAgent() {
        return "SDK/" . __CLASS__ . " " . php_uname('s') . " " . php_uname('r') . " " . phpversion();
    }

    public static function parseHeaders($raw) {
        $headers = [];
        $lines = explode("\r\n", $raw);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $key = ucwordsHyphen(trim($key));
                $headers[$key] = trim($value);
            }
        }
        return $headers;
    }

    public static function escapeQuotes($str) {
        return str_replace('"', '\\"', $str);
    }

    public static function ucwordsHyphen($str) {
        return str_replace(' ', '-', ucwords(str_replace('-', ' ', $str)));
    }
