    private static function ucwordsHyphen($str) {
        return ucwords(str_replace(' ', '-', strtolower($str)));
    }

    private static function createMultipartData($fields, $name, $fileName, $fileBody, $mimeType) {
        $boundary = uniqid();
        $multipartData = "";
        foreach ($fields as $key => $value) {
            $multipartData .= "--$boundary" . PHP_EOL;
            $multipartData .= "Content-Disposition: form-data; name=\"" . self::escapeQuotes($key) . "\"" . PHP_EOL . PHP_EOL;
            $multipartData .= $value . PHP_EOL . PHP_EOL;
        }
        if ($fileBody !== null) {
            $multipartData .= "--$boundary" . PHP_EOL;
            $multipartData .= "Content-Disposition: form-data; name=\"" . self::escapeQuotes($name) . "\"; filename=\"" . self::escapeQuotes($fileName) . "\"" . PHP_EOL;
            $multipartData .= "Content-Type: " . ($mimeType !== null ? $mimeType : "application/octet-stream") . PHP_EOL . PHP_EOL;
            $multipartData .= $fileBody . PHP_EOL . PHP_EOL;
        }
        $multipartData .= "--$boundary--" . PHP_EOL;
        return "--$boundary" . PHP_EOL . $multipartData;
    }
}
