        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_FILE, $fp);
        curl_setopt($ch, CURLOPT_HEADER, false);
        curl_setopt($ch, CURLOPT_NOBODY, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $headerSize ? $headerSize : 10);
        curl_exec($ch);
        $httpcode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        if ($httpcode == 200) {
            fclose($fp);
            return ['success' => true];
        }
        return ['error' => 'Failed to download file'];
    }

    private static function fileDownloadCurl($url, $fileName, $supportRange, $existsLength, $length) {
