        curl_setopt($ch, CURLOPT_RANGE, $existsLength . '-' . ($existsLength + $length - 1));
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            'Range: bytes=' . $existsLength . '-'
        ]);
        curl_exec($ch);
        $httpcode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        fclose($fp);
        if ($httpcode == 206) {
            return ['success' => true];
        }
        return ['error' => 'Failed to download file'];
    }

    private static function checkGzip($file) {
