            $fp = fopen($file, 'rb');
            $fileContent = fread($fp, 2);
            fclose($fp);
            if (substr($fileContent, 0, 1) == "\x1f" && substr($fileContent, 1, 1) == "\x8b") {
                $fp = fopen($file, 'rb');
                $fileContent = gzdecode(fread($fp, filesize($file)));
                fclose($fp);
                file_put_contents($file, $fileContent);
            }
        }
    }
