class Bcs {
    private $rootPath;
    private $error;
    private $config;
    private $bcs;

    public function __construct(array $config = []) {
        $this->config = $config;
        $this->bcs = new BaiduBCS($config['AccessKey'], $config['SecretKey'], $config['bucket']);
        $this->rootPath = $config['rootPath'] ?? '/';
    }

    private function request(string $path, string $method, array $headers = null, $body = null): mixed {
        $curl = curl_init();
        $url = $this->bcs->getEndpoint() . $this->rootPath . $path;

        curl_setopt($curl, CURLOPT_URL, $url);
        curl_setopt($curl, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl, CURLOPT_HTTPHEADER, $headers ?? []);

        if ($body !== null) {
            curl_setopt($curl, CURLOPT_POSTFIELDS, $body);
        }

        $response = curl_exec($curl);
        $error = curl_error($curl);
        $httpcode = curl_getinfo($curl, CURLINFO_HTTP_CODE);

        curl_close($curl);

        if ($error) {
            $this->error = $error;
            return null;
        }

        return $this->response($response);
    }

    private function response(string $text): array {
        return json_decode($text, true);
    }

    private function error(string $header): void {
        if (strpos($header, '404') !== false) {
            $this->error = 'Resource not found';
        } elseif (strpos($header, '403') !== false) {
            $this->error = 'Access denied';
        } else {
            $this->error = 'Unknown error';
        }
    }
}
