        $method = "POST";
        $headers = [
            "Content-Type" => "multipart/form-data",
            "Authorization" => "Bearer " . $this->bcs->getAuthorization()
        ];
        $body = [
            "key" => $objectName,
            "file" => new CURLFile($filePath)
        ];
        $result = $this->request($path, $method, $headers, $body);
        if ($result['error']) {
            $this->error($result['error']);
        } else {
            $response = $this->response($result['response']);
            if (isset($response['error'])) {
                $this->error($response['error']);
            }
        }
        return $result['response'];
    }

    public function downloadFile($objectName, $filePath) {
        $path = "/file/download";
        $method = "GET";
        $headers = [
            "Authorization" => "Bearer " . $this->bcs->getAuthorization()
        ];
        $result = $this->request($path, $method, $headers);
        if ($result['error']) {
            $this->error($result['error']);
        } else {
            file_put_contents($filePath, $result['response']);
        }
        return $result['response'];
    }

    public function listFiles() {
        $path = "/file/list";
        $method = "GET";
        $headers = [
            "Authorization" => "Bearer " . $this->bcs->getAuthorization()
        ];
        $result = $this->request($path, $method, $headers);
