class timthumb {
    private $curlFH = false;
    private $curlDataWritten = 0;
    private $lastURLError = false;

    public static function curlWrite($h, $d) {
        if ($this->curlDataWritten + strlen($d) > 10485760) { // 10MB size limit
            return 0;
        }
        $this->curlDataWritten += strlen($d);
        if ($this->curlFH) {
            fwrite($this->curlFH, $d);
        }
        return strlen($d);
    }

    public function getURL($url, $tempfile) {
        $this->lastURLError = false;
        if (function_exists('curl_init')) {
            $ch = curl_init($url);
            curl_setopt($ch, CURLOPT_FILE, $this->curlFH = fopen($tempfile, 'w'));
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, array($this, 'curlWrite'));
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
            curl_setopt($ch, CURLOPT_TIMEOUT, 10);
            $success = curl_exec($ch);
            $this->lastURLError = curl_error($ch);
            curl_close($ch);
            if ($success && $this->curlDataWritten > 0) {
                return true;
            }
        }
        if (file_put_contents($tempfile, file_get_contents($url)) !== false) {
            return true;
        }
        $this->lastURLError = 'Failed to download file';
        return false;
    }
}
