        curl_setopt($this->curl_handle, CURLOPT_USERAGENT, $this->useragent);
        curl_setopt($this->curl_handle, CURLOPT_TIMEOUT, $this->timeout);
        curl_setopt($this->curl_handle, CURLOPT_CONNECTTIMEOUT, $this->connect_timeout);

        if ($this->username && $this->password) {
            curl_setopt($this->curl_handle, CURLOPT_HTTPAUTH, CURLAUTH_ANY);
            curl_setopt($this->curl_handle, CURLOPT_USERPWD, $this->username . ':' . $this->password);
        }

        if ($this->proxy) {
            curl_setopt($this->curl_handle, CURLOPT_PROXY, $this->proxy);
        }

        if ($this->ssl_verification) {
            curl_setopt($this->curl_handle, CURLOPT_SSL_VERIFYPEER, true);
            curl_setopt($this->curl_handle, CURLOPT_SSL_VERIFYHOST, 2);
            curl_setopt($this->curl_handle, CURLOPT_CAINFO, $this->cacert_location);
        }

        if ($this->read_file) {
            curl_setopt($this->curl_handle, CURLOPT_INFILE, $this->read_file);
            curl_setopt($this->curl_handle, CURLOPT_INFILESIZE, filesize($this->read_file));
        }

        if ($this->write_file) {
            curl_setopt($this->curl_handle, CURLOPT_OUTFILE, $this->write_file);
        }

        if ($this->seek_position) {
            fseek($this->read_stream, $this->seek_position);
        }

        if ($this->read_stream) {
            curl_setopt($this->curl_handle, CURLOPT_READFUNCTION, array($this, 'streaming_read_callback'));
            curl_setopt($this->curl_handle, CURLOPT_FILE, $this->read_stream);
        }

        if ($this->write_stream) {
            curl_setopt($this->curl_handle, CURLOPT_WRITEFUNCTION, array($this, 'streaming_write_callback'));
            curl_setopt($this->curl_handle, CURLOPT_BINARYTRANSFER, true);
            curl_setopt($this->curl_handle, CURLOPT_POSTFIELDS, $this->write_stream);
        }

        foreach ($this->curlopts as $opt => $value) {
            curl_setopt($this->curl_handle, $opt, $value);
        }

        return $this->curl_handle;
    }

    public function process_response(resource $curl_handle = null, string $response = null): mixed {
        if (!$curl_handle) {
            $curl_handle = $this->prep_request();
        }

        $this->response = curl_exec($curl_handle);
        $this->response_headers = curl_getheader($curl_handle);
        $this->response_code = curl_getinfo($curl_handle, CURLINFO_HTTP_CODE);
        $this->response_info = curl_getinfo($curl_handle);
        curl_close($curl_handle);

        return $this->response;
    }

    public function send_request(bool $parse = false): mixed {
        return $this->process_response();
    }

    public function send_multi_request(array $handles, array $opt = null): array {
        $mh = curl_multi_init();
        foreach ($handles as $handle) {
            curl_multi_add_handle($mh, $handle);
        }

        $running = null;
        do {
            curl_multi_exec($mh, $running);
            curl_multi_select($mh);
        } while ($running);

        $responses = [];
        foreach ($handles as $handle) {
            $responses[] = curl_multi_getcontent($handle);
            curl_multi_remove_handle($mh, $handle);
        }

        curl_multi_close($mh);

        return $responses;
    }
