        return defined('ONLINE_CONVERT_API_KEY') && ONLINE_CONVERT_API_KEY !== '' && !defined('DISABLE_ONLINE_CONVERT');
    }

    public function init() {
        return array(
            'api_available' => defined('ONLINE_CONVERT_API_KEY') && function_exists('curl_init')
        );
    }

    public function api() {
        $params = array(
            'category' => $this->category,
            'convert' => $this->convert,
            'options' => is_string($this->options) ? $this->options : json_encode($this->options),
            'source' => $this->source,
            'filename' => $this->filename,
            'mime' => $this->mime,
            'jobid' => $this->jobid
        );

        if ($this->jobid) {
            // Check job status
            $url = "https://api.online-convert.com/v1/jobs/status?job_id=" . urlencode($this->jobid);
            $response = $this->curlRequest($url);
        } else {
            // Create new conversion job
            $url = "https://api.online-convert.com/v1/jobs/create";
            $response = $this->curlRequest($url, $params);
        }

        return $response;
    }

    protected function curlRequest($url, $data = array()) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data));
        curl_setopt($ch, CURLOPT_HTTPHEADER, array(
            'Authorization: Bearer ' . ONLINE_CONVERT_API_KEY,
            'Content-Type: application/x-www-form-urlencoded'
        ));
        $response = curl_exec($ch);
        $error = curl_error($ch);
        $httpcode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        if ($error) {
            return array('error' => $error);
        } elseif ($httpcode != 200) {
            return array('error' => 'HTTP error: ' . $httpcode);
        }

        return json_decode($response, true);
    }
