        if (isset($request->headers)) {
            curl_setopt($curl, CURLOPT_HTTPHEADER, $request->headers);
        }

        $result = curl_exec($curl);

        if (curl_errno($curl)) {
            $response->error = curl_error($curl);
            $response->statusCode = curl_errno($curl);
        } else {
            $response->statusCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);
            $response->body = $result;
            $response->headers = $this->getHeaders($result);
        }

        curl_close($curl);
        return $response;
    }

