        $headers = [];
        $headersPattern = '/\r\n([^\r\n]+):\s*([^\r\n]*)\r\n/';
        preg_match_all($headersPattern, $body, $matches);
        foreach ($matches[1] as $key => $value) {
            $headers[$value] = $matches[2][$key];
        }
        return $headers;
