                curl_setopt($curl, CURLOPT_POST, true);
                curl_setopt($curl, CURLOPT_POSTFIELDS, $request->body);
                break;
            case 'PUT':
                curl_setopt($curl, CURLOPT_CUSTOMREQUEST, 'PUT');
                curl_setopt($curl, CURLOPT_POSTFIELDS, $request->body);
                break;
            case 'DELETE':
                curl_setopt($curl, CURLOPT_CUSTOMREQUEST, 'DELETE');
                break;
            default:
                $response->errors[] = 'Unsupported HTTP method';
                curl_close($curl);
                return $response;
        }

        $result = curl_exec($curl);
        if ($result === false) {
            $response->errors[] = curl_error($curl);
        } else {
            $response->statusCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);
            $response->body = $result;
            $response->headers = get_headers($url);
