        $headers = [];
        $headersPattern = '/\r\n(\S+):\s*(\S+)/';
        preg_match_all($headersPattern, $body, $matches, PREG_SET_ORDER);
        foreach ($matches as $match) {
            $headers[$match[1]] = $match[2];
        }
        return $headers;
    }
}
