class SpotlightClient {
    public function sendRequest(Request $request, string $url): Response {
        if (!extension_loaded('curl')) {
            throw new Exception('cURL extension is not loaded');
        }

        $response = new Response();
        $ch = curl_init();

        $this->validateRequest($request);
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $request->headers);
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

        switch ($request->method) {
            case 'GET':
                curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'GET');
                break;
            case 'POST':
                curl_setopt($ch, CURLOPT_POST, true);
                curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
                break;
            case 'PUT':
                curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'PUT');
                curl_setopt($ch, CURLOPT_POSTFIELDS, $request->body);
                break;
            case 'DELETE':
                curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'DELETE');
                break;
            default:
                $response->errors[] = 'Unsupported HTTP method';
                curl_close($ch);
                return $response;
        }

        $result = curl_exec($ch);
        if (curl_errno($ch)) {
            $response->errors[] = curl_error($ch);
        } else {
            $response->statusCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
            $response->body = $result;
            $response->headers = $this->getHeaders($result);
        }

        curl_close($ch);
        return $response;
    }

    private function validateRequest(Request $request) {
        if (empty($request->method)) {
            throw new Exception('HTTP method is required');
        }
        if (empty($request->headers)) {
            throw new Exception('Headers are required');
        }
    }

    private function getHeaders(string $body) {
        $headers = [];
        $header_size = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $header = substr($body, 0, $header_size);
        $lines = explode("\r\n", $header);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[trim($key)] = trim($value);
            }
        }
        return $headers;
    }
}
