function extractWeChatArticleInfo($url) {
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
    $html = curl_exec($ch);
    curl_close($ch);

    $title = '';
    $cover = '';
    if (preg_match('/<title>(.*?)<\/title>/i', $html, $matches)) {
        $title = $matches[1];
    }
    if (preg_match('/<img[^>]*src="([^"]*)"[^>]*>/i', $html, $matches)) {
        $cover = $matches[1];
        if (strpos($cover, 'http') !== 0) {
            $cover = 'https://weserv.nl/?url=' . urlencode($cover);
        }
    }

    return [
        'title' => $title,
        'cover' => $cover,
        'success' => !empty($title) && !empty($cover)
    ];
}
