    private $curlDataWritten = 0;
    private $lastURLError = null;

    public static function curlWrite($h, $d) {
        if ($this->curlFH === false) return 0;
        $bytes = strlen($d);
        if ($this->curlDataWritten + $bytes > 1048576) { // 1MB size limit
            return 0;
        }
        fwrite($this->curlFH, $d);
        $this->curlDataWritten += $bytes;
        return $bytes;
    }

    public function getURL($url, $tempfile) {
        $this->lastURLError = null;
        if (!function_exists('curl_init')) {
            $this->lastURLError = 'cURL is not available';
            return false;
        }
        $ch = curl_init($url);
        if ($ch === false) {
            $this->lastURLError = curl_error($ch);
            return false;
        }
        $fp = fopen($tempfile, 'wb');
        if ($fp === false) {
            curl_close($ch);
            $this->lastURLError = 'Failed to open file';
            return false;
        }
        $this->curlFH = $fp;
        curl_setopt($ch, CURLOPT_FILE, $this->curlFH);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 10);
        $success = curl_exec($ch);
        $this->curlFH = false;
        if ($success === false) {
            $this->lastURLError = curl_error($ch);
            fclose($fp);
            curl_close($ch);
            return false;
        }
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        if ($httpCode >= 400) {
            fclose($fp);
            curl_close($ch);
            return false;
        }
        fclose($fp);
        curl_close($ch);
        return true;
