    $cache_dir = 'cache';
    $cache_file = $cache_dir . '/' . $basename;
    $default_file = $default_ico;
    $default_content = file_get_contents($default_file);
    $default_mimetype = mime_content_type($default_file);
    $default_last_modified = filemtime($default_file);
    $default_etag = md5($default_content);

    if (!file_exists($cache_dir)) {
        mkdir($cache_dir, 0777, true);
    }

    $headers = headers_get_all();
    $if_none_match = null;
    $if_modified_since = null;

    foreach ($headers as $header) {
        if (strtolower($header[0]) == 'if-none-match') {
            $if_none_match = $header[1];
        }
        if (strtolower($header[0]) == 'if-modified-since') {
            $if_modified_since = strtotime($header[1]);
        }
    }

    if (file_exists($cache_file) && $if_none_match == $default_etag && ($if_modified_since === false || filemtime($cache_file) > $if_modified_since)) {
        header('HTTP/1.1 304 Not Modified');
        exit;
    }

    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url . '/' . $basename);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
    curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
    curl_setopt($ch, CURLOPT_TIMEOUT, 10);
    $response = curl_exec($ch);
    $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
    $content_type = curl_getinfo($ch, CURLINFO_CONTENT_TYPE);
    curl_close($ch);

    if ($http_code == 200 && $content_type == 'image/x-icon') {
        file_put_contents($cache_file, $response);
        $mimetype = $content_type;
        $last_modified = filemtime($cache_file);
        $etag = md5($response);
    } else {
        $response = $default_content;
        $mimetype = $default_mimetype;
        $last_modified = $default_last_modified;
        $etag = $default_etag;
    }

    header('Content-Type: ' . $mimetype);
    header('Cache-Control: public, max-age=' . $cache_time);
    header('Last-Modified: ' . date('D, d M Y H:i:s', $last_modified) . ' GMT');
    header('ETag: "' . $etag . '"');
    header('Expires: ' . date('D, d M Y H:i:s', time() + $cache_time) . ' GMT');
    echo $response;
}
