class BackendService
{
    private $backendUrl;
    private $backendToken;

    public function __construct()
    {
        $this->backendUrl = getenv('BACKEND_URL');
        $this->backendToken = getenv('BACKEND_TOKEN');
    }

    public function addTask(string $id): array
    {
        return $this->setCurl($this->backendUrl, $this->backendToken, 'add', $id);
    }

    public function restartTask(string $id): array
    {
        return $this->setCurl($this->backendUrl, $this->backendToken, 'restart', $id);
    }

    public function removeTask(string $id): array
    {
        return $this->setCurl($this->backendUrl, $this->backendToken, 'remove', $id);
    }

    private function setCurl(string $backendUrl, string $backendToken, string $operation, string $id): array
    {
        $curl = curl_init();
        curl_setopt($curl, CURLOPT_URL, $backendUrl . '/' . $operation . '/' . $id);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl, CURLOPT_HTTPHEADER, [
            'Authorization: Bearer ' . $backendToken,
            'Content-Type: application/json'
        ]);
        $response = curl_exec($curl);
        $httpcode = curl_getinfo($curl, CURLINFO_HTTP_CODE);
        curl_close($curl);

        if ($response === false) {
            return ['status' => 'error', 'message' => curl_error($curl)];
        }

        return ['status' => 'success', 'message' => $response];
    }
}
