    private static $apiKey = 'YOUR_API_KEY_HERE';

    public static function translate(string $source, string $target, string $text): string {
        if (strlen($text) > 9000) {
            throw new Exception('Input text exceeds 9000 characters.');
        }
        return self::requestTranslation($source, $target, $text);
    }

    private static function requestTranslation(string $source, string $target, string $text): string {
        $url = 'https://translation.googleapis.com/language/translate/v2?key=' . self::$apiKey;
        $data = [
            'q' => $text,
            'source' => $source,
            'target' => $target,
            'format' => 'text'
        ];

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        $response = curl_exec($ch);
        curl_close($ch);

        return $response;
    }

    private static function getSentencesFromJSON(string $json): string {
        $data = json_decode($json, true);
        $translations = $data['data']['translations'];
        $translatedText = '';
        foreach ($translations as $translation) {
            $translatedText .= $translation['translatedText'] . ' ';
        }
        return trim($translatedText);
    }
}
