class GoogleTranslate {
    private static $apiUrl = 'https://translate.googleapis.com/translate_a/single';

    public static function translate(string $source, string $target, string $text): string {
        if (strlen($text) > 9000) {
            throw new Exception('Input text exceeds 9000 characters.');
        }
        return self::requestTranslation($source, $target, $text);
    }

    private static function requestTranslation(string $source, string $target, string $text): string {
        $data = http_build_query([
            'client' => 'gtx',
            'sl' => $source,
            'tl' => $target,
            'dt' => 't',
            'q' => urlencode($text)
        ]);

        $options = [
            CURLOPT_URL => self::$apiUrl,
            CURLOPT_POST => true,
            CURLOPT_POSTFIELDS => $data,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_SSL_VERIFYPEER => false
        ];

        $ch = curl_init();
        curl_setopt_array($ch, $options);
        $response = curl_exec($ch);
        curl_close($ch);

        return $response;
    }

    private static function getSentencesFromJSON(string $json): string {
        $data = json_decode($json, true);
        $translatedText = '';
        foreach ($data as $sentence) {
            foreach ($sentence as $part) {
                if (is_array($part)) {
                    $translatedText .= implode(' ', $part);
                }
            }
        }
        return $translatedText;
    }
}
