        $this->sizeLimit = $sizeLimit;
    }

    private function curlInit($url) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 1);
        curl_setopt($ch, CURLOPT_HEADER, 0);
        return $ch;
    }

    private function getImageSize($url) {
        $ch = $this->curlInit($url);
        curl_setopt($ch, CURLOPT_NOBODY, 1);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 0);
        curl_setopt($ch, CURLOPT_FILETIME, 1);
        curl_exec($ch);
        $info = curl_getinfo($ch);
        curl_close($ch);
        return $info;
    }

    private function compressImage($imageData) {
        $image = imagecreatefromstring($imageData);
        ob_start();
        imagejpeg($image, null, 75);
        $compressedData = ob_get_contents();
        ob_end_clean();
        imagedestroy($image);
        return $compressedData;
    }

    public function downloadImage($url) {
        $info = $this->getImageSize($url);
        if ($info['download_content_length'] > $this->compressLimit) {
            throw new Exception("Image size exceeds compression limit.");
        }

        $ch = $this->curlInit($url);
        $imageData = curl_exec($ch);
        if (curl_errno($ch)) {
            curl_close($ch);
            throw new Exception("Error downloading image: " . curl_error($ch));
        }
        curl_close($ch);

        $image = imagecreatefromstring($imageData);
        $width = imagesx($image);
        $height = imagesy($image);
        if ($width > $this->sizeLimit || $height > $this->sizeLimit) {
            imagedestroy($image);
            throw new Exception("Image dimensions exceed size limit.");
        }

        if ($info['download_content_length'] > $this->compressLimit) {
            $imageData = $this->compressImage($imageData);
        }

        return $imageData;
    }
}
