    $assign = isset($params['assign']) ? $params['assign'] : null;
    $assign_headers = isset($params['assign_headers']) ? $params['assign_headers'] : null;
    $user = isset($params['user']) ? $params['user'] : null;
    $pass = isset($params['pass']) ? $params['pass'] : null;
    $proxy_host = isset($params['proxy_host']) ? $params['proxy_host'] : null;
    $proxy_port = isset($params['proxy_port']) ? $params['proxy_port'] : null;

    if (!$template->security->isTrustedUri($file) || !$template->security->isTrustedDir($file)) {
        return;
    }

    $content = '';
    $headers = '';

    if (filter_var($file, FILTER_VALIDATE_URL) === false) {
        if (file_exists($file)) {
            $content = file_get_contents($file);
        }
    } else {
        $host = parse_url($file, PHP_URL_HOST);
        $port = parse_url($file, PHP_URL_PORT) ?: 80;
        $path = parse_url($file, PHP_URL_PATH);
        $query = parse_url($file, PHP_URL_QUERY) ? '?' . parse_url($file, PHP_URL_QUERY) : '';

        $fp = fsockopen(($proxy_host ?: $host), ($proxy_port ?: $port), $errno, $errstr, 30);
        if (!$fp) {
            return;
        }

        $out = "GET " . ($proxy_host ? $file : $path . $query) . " HTTP/1.1\r\n";
        $out .= "Host: " . $host . "\r\n";
        if ($user && $pass) {
            $out .= "Authorization: Basic " . base64_encode("$user:$pass") . "\r\n";
        }
        $out .= "Connection: Close\r\n\r\n";

        fwrite($fp, $out);
        while (!feof($fp)) {
            $line = fgets($fp, 128);
            if ($line == "\r\n") {
                break;
            }
            $headers .= $line;
        }
        while (!feof($fp)) {
            $content .= fgets($fp, 128);
        }
        fclose($fp);
    }

    if ($assign) {
        $template->assign($assign, $content);
    }
    if ($assign_headers) {
        $template->assign($assign_headers, $headers);
    }

    return $assign ? null : $content;
}
