<?php

class CI_Trackback {
    private $charset = 'UTF-8';
    private $data = [];
    private $convert_ascii = TRUE;
    private $response = '';
    private $error_msg = [];

    public function process($url, $data) {
        $fp = @fsockopen(parse_url($url, PHP_URL_HOST), 80, $errno, $errstr, 30);
        if (!$fp) {
            $this->set_error($errstr);
            return FALSE;
        }

        $path = parse_url($url, PHP_URL_PATH);
        $query = parse_url($url, PHP_URL_QUERY) ? '?' . parse_url($url, PHP_URL_QUERY) : '';
        $out = "POST {$path}{$query} HTTP/1.1\r\n";
        $out .= "Host: " . parse_url($url, PHP_URL_HOST) . "\r\n";
        $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
        $out .= "Content-Length: " . strlen($data) . "\r\n";
        $out .= "Connection: Close\r\n\r\n";
        $out .= $data;

        fwrite($fp, $out);
        while (!feof($fp)) {
            $this->response .= fgets($fp, 128);
        }
        fclose($fp);

        if (strpos($this->response, '200 OK') === FALSE) {
            $this->set_error('Trackback response error');
            return FALSE;
        }

        return TRUE;
    }

    public function get_id($url) {
        if (preg_match('/[?&]id=([0-9]+)/', $url, $matches)) {
            return $matches[1];
        }
        return FALSE;
    }

    public function set_error($msg) {
        $this->error_msg[] = $msg;
    }
}

