<?php

class soap_transport_http {
    private $url;
    private $uri;
    private $scheme;
    private $host;
    private $port;
    private $path;
    private $request_method = 'POST';
    private $protocol_version = '1.0';
    private $outgoing_headers = [];
    private $incoming_headers = [];
    private $outgoing_payload;
    private $incoming_payload;
    private $persistentConnection = false;
    private $use_curl = false;
    private $proxy = null;
    private $curl_handle;

    public function __construct($url, $curl_options = null, $use_curl = false) {
        $this->url = $url;
        $this->use_curl = $use_curl;
        if ($this->use_curl) {
            $this->curl_handle = curl_init();
            if ($curl_options) {
                foreach ($curl_options as $option => $value) {
                    curl_setopt($this->curl_handle, $option, $value);
                }
            }
        }
        $parsed_url = parse_url($url);
        $this->scheme = $parsed_url['scheme'];
        $this->host = $parsed_url['host'];
        $this->port = isset($parsed_url['port']) ? $parsed_url['port'] : ($this->scheme === 'https' ? 443 : 80);
        $this->path = isset($parsed_url['path']) ? $parsed_url['path'] : '/';
        $this->uri = $this->scheme . '://' . $this->host . $this->path;
    }

    public function connect($connection_timeout = 0, $response_timeout = 30) {
        if ($this->use_curl) {
            curl_setopt($this->curl_handle, CURLOPT_URL, $this->url);
            curl_setopt($this->curl_handle, CURLOPT_CONNECTTIMEOUT, $connection_timeout);
            curl_setopt($this->curl_handle, CURLOPT_TIMEOUT, $response_timeout);
            return true;
        } else {
            $errno = 0;
            $errstr = '';
            $fp = fsockopen($this->host, $this->port, $errno, $errstr, $connection_timeout);
            if (!$fp) {
                return false;
            }
            stream_set_timeout($fp, $response_timeout);
            return $fp;
        }
    }

    public function send($data, $timeout = 0, $response_timeout = 30, $cookies = null) {
        $this->outgoing_payload = $data;
        if ($this->sendRequest($data, $cookies)) {
            return $this->getResponse();
        }
        return false;
    }

    private function sendRequest($data, $cookies = null) {
        if ($this->use_curl) {
            curl_setopt($this->curl_handle, CURLOPT_POSTFIELDS, $data);
            if ($cookies) {
                $cookie_str = '';
                foreach ($cookies as $name => $value) {
                    $cookie_str .= $name . '=' . $value . '; ';
                }
                curl_setopt($this->curl_handle, CURLOPT_COOKIE, rtrim($cookie_str, '; '));
            }
            foreach ($this->outgoing_headers as $name => $value) {
                $headers[] = "$name: $value";
            }
            curl_setopt($this->curl_handle, CURLOPT_HTTPHEADER, $headers);
            return true;
        } else {
            $request = "{$this->request_method} {$this->path} HTTP/{$this->protocol_version}\r\n";
            $request .= "Host: {$this->host}\r\n";
            foreach ($this->outgoing_headers as $name => $value) {
                $request .= "$name: $value\r\n";
            }
            if ($cookies) {
                $cookie_str = '';
                foreach ($cookies as $name => $value) {
                    $cookie_str .= $name . '=' . $value . '; ';
                }
                $request .= "Cookie: " . rtrim($cookie_str, '; ') . "\r\n";
            }
            $request .= "Content-Length: " . strlen($data) . "\r\n";
            $request .= "\r\n";
            $request .= $data;
            $fp = $this->connect();
            if (!$fp) {
                return false;
            }
            fwrite($fp, $request);
            return $fp;
        }
    }

    private function getResponse() {
        if ($this->use_curl) {
            $response = curl_exec($this->curl_handle);
            if ($response === false) {
                return false;
            }
            $header_size = curl_getinfo($this->curl_handle, CURLINFO_HEADER_SIZE);
            $this->incoming_headers = $this->parseHeaders(substr($response, 0, $header_size));
            $this->incoming_payload = substr($response, $header_size);
            return $this->incoming_payload;
        } else {
            $response = '';
            $fp = $this->connect();
            while (!feof($fp)) {
                $response .= fgets($fp, 4096);
            }
            fclose($fp);
            list($headers, $body) = explode("\r\n\r\n", $response, 2);
            $this->incoming_headers = $this->parseHeaders($headers);
            $this->incoming_payload = $body;
            return $this->incoming_payload;
        }
    }

    public function setHeader($name, $value) {
        $this->outgoing_headers[$name] = $value;
    }

    public function setCurlOption($option, $value) {
        if ($this->use_curl) {
            curl_setopt($this->curl_handle, $option, $value);
        }
    }

    private function parseHeaders($header_str) {
        $headers = [];
        $lines = explode("\r\n", $header_str);
        foreach ($lines as $line) {
            if (strpos($line, ':') !== false) {
                list($name, $value) = explode(':', $line, 2);
                $headers[trim($name)] = trim($value);
            }
        }
        return $headers;
    }

    public function parseCookie($cookie_str) {
        $cookies = [];
        $cookie_pairs = explode('; ', $cookie_str);
        foreach ($cookie_pairs as $cookie) {
            list($name, $value) = explode('=', $cookie, 2);
            $cookies[trim($name)] = trim($value);
        }
        return $cookies;
    }
