    $proxy_host = isset($params['proxy_host']) ? $params['proxy_host'] : '';
    $proxy_port = isset($params['proxy_port']) ? $params['proxy_port'] : '';

    $fp = fsockopen(($proxy_host ? $proxy_host : $host), ($proxy_host ? $proxy_port : $port), $errno, $errstr, $timeout);
    if (!$fp) {
        trigger_error("Socket error: $errstr ($errno)", E_USER_NOTICE);
        return;
    }

    $request = "GET " . ($proxy_host ? $params['file'] : $path . $query) . " HTTP/1.0\r\n";
    $request .= "Host: $host\r\n";
    if (isset($params['user']) && isset($params['pass'])) {
