```php
<?php

class Http
{
    public static function fsockopenDownload($url, $conf = array())
    {
        $defaultConf = array(
            'limit' => 0,
            'post' => '',
            'cookie' => '',
            'ip' => '',
            'timeout' => 15,
            'block' => true
        );
        $conf = array_merge($defaultConf, $conf);

        $urlParts = parse_url($url);
        $host = $urlParts['host'];
        $port = isset($urlParts['port']) ? $urlParts['port'] : 80;
        $path = isset($urlParts['path']) ? $urlParts['path'] : '/';
        $query = isset($urlParts['query']) ? '?' . $urlParts['query'] : '';

        $ip = $conf['ip'] ? $conf['ip'] : $host;
        $timeout = $conf['timeout'];
        $blocking = $conf['block'] ? 1 : 0;

        $fp = @fsockopen($ip, $port, $errno, $errstr, $timeout);
        if (!$fp) {
            return '';
        }

        stream_set_blocking($fp, $blocking);
        stream_set_timeout($fp, $timeout);

        $request = ($conf['post']) ? 'POST' : 'GET';
        $postContent = is_array($conf['post']) ? http_build_query($conf['post']) : $conf['post'];

        $headers = array(
            "$request $path$query HTTP/1.0",
            "Host: $host",
            "User-Agent: PHP fsockopen HTTP client",
            "Connection: Close"
        );

        if ($conf['cookie']) {
            $headers[] = "Cookie: " . $conf['cookie'];
        }

        if ($conf['post']) {
            $headers[] = "Content-Type: application/x-www-form-urlencoded";
            $headers[] = "Content-Length: " . strlen($postContent);
        }

        $requestHeaders = implode("\r\n", $headers) . "\r\n\r\n" . $postContent;

        fwrite($fp, $requestHeaders);

        $response = '';
        while (!feof($fp)) {
            $chunk = fgets($fp, $conf['limit'] ? $conf['limit'] : 4096);
            $response .= $chunk;
        }

        fclose($fp);

        $bodyStart = strpos($response, "\r\n\r\n") + 4;
        return substr($response, $bodyStart);
    }
}

?>
