function _dfsockopen($url, $limit = 0, $post = '', $cookie = '', $bysocket = false, $ip = '', $timeout = 15, $block = true, $encodetype = 'URLENCODE', $allowcurl = true, $position = 0, $files = array()) {
    $GLOBALS['filesockheader'] = '';
    $return = '';
    $urlarr = parse_url($url);
    $host = $urlarr['host'];
    $port = isset($urlarr['port']) ? $urlarr['port'] : ($urlarr['scheme'] == 'https' ? 443 : 80);
    $path = $urlarr['path'] ? $urlarr['path'] . (isset($urlarr['query']) ? '?' . $urlarr['query'] : '') : '/';
    $scheme = $urlarr['scheme'];
    $boundary = '';

    if ($post) {
        if (is_array($post)) {
            if ($encodetype == 'URLENCODE') {
                $post = http_build_query($post);
            } else {
                $boundary = '----WebKitFormBoundary' . md5(microtime());
                $postdata = '';
                foreach ($post as $k => $v) {
                    $postdata .= "--$boundary\r\n";
                    $postdata .= "Content-Disposition: form-data; name=\"$k\"\r\n\r\n$v\r\n";
                }
                foreach ($files as $k => $file) {
                    $postdata .= "--$boundary\r\n";
                    $postdata .= "Content-Disposition: form-data; name=\"$k\"; filename=\"" . basename($file) . "\"\r\n";
                    $postdata .= "Content-Type: application/octet-stream\r\n\r\n";
                    $postdata .= file_get_contents($file) . "\r\n";
                }
                $postdata .= "--$boundary--\r\n";
                $post = $postdata;
            }
        }
    }

    if (function_exists('curl_init') && $allowcurl) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $scheme . '://' . ($ip ? $ip : $host) . ($port == 80 ? '' : ':' . $port) . $path);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        if ($post) {
            curl_setopt($ch, CURLOPT_POST, 1);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $post);
            if ($boundary) {
                curl_setopt($ch, CURLOPT_HTTPHEADER, array('Content-Type: multipart/form-data; boundary=' . $boundary));
            }
        }
        if ($cookie) {
            curl_setopt($ch, CURLOPT_COOKIE, $cookie);
        }
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);
        curl_setopt($ch, CURLOPT_TIMEOUT, $timeout);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        $data = curl_exec($ch);
        $GLOBALS['filesockheader'] = curl_getinfo($ch, CURLINFO_HEADER_OUT);
        if (curl_errno($ch) || curl_getinfo($ch, CURLINFO_HTTP_CODE) != 200) {
            $data = '';
        }
        curl_close($ch);
    } else {
        $fp = fsockopen(($scheme == 'https' ? 'ssl://' : '') . ($ip ? $ip : $host), $port, $errno, $errstr, $timeout);
        if (!$fp) {
            return '';
        }
        stream_set_blocking($fp, $block);
        stream_set_timeout($fp, $timeout);
        $header = "GET $path HTTP/1.1\r\n";
        if ($post) {
            $header = "POST $path HTTP/1.1\r\n";
        }
        $header .= "Host: $host\r\n";
        if ($cookie) {
            $header .= "Cookie: $cookie\r\n";
        }
        $header .= "Connection: Close\r\n";
        if ($post) {
            $header .= "Content-Length: " . strlen($post) . "\r\n";
            if ($boundary) {
                $header .= "Content-Type: multipart/form-data; boundary=$boundary\r\n";
            } else {
                $header .= "Content-Type: application/x-www-form-urlencoded\r\n";
            }
        }
        $header .= "\r\n" . $post;
        fwrite($fp, $header);
        while (!feof($fp)) {
            $data = fgets($fp, 128);
            if ($limit && strlen($return) >= $limit) {
                break;
            }
            $return .= $data;
        }
        fclose($fp);
        $GLOBALS['filesockheader'] = $header;
        $return = explode("\r\n\r\n", $return, 2);
        $return = isset($return[1]) ? $return[1] : '';
    }
    return $return;
}
