<?php

namespace MyNamespace;

use Psr\Http\Client\ClientInterface;
use Psr\Http\Message\RequestInterface;
use Psr\Http\Message\ResponseInterface;
use Psr\Log\LoggerInterface;
use Psr\Log\LogLevel;

class SocketHttpClient implements ClientInterface
{
    private $logger;

    public function __construct(LoggerInterface $logger = null)
    {
        $this->logger = $logger;
    }

    public function sendRequest(RequestInterface $request): ResponseInterface
    {
        $uri = $request->getUri();
        $scheme = $uri->getScheme();
        $host = $uri->getHost();
        $port = $uri->getPort() ?: ($scheme === 'https' ? 443 : 80);
        $path = $uri->getPath() ?: '/';
        $query = $uri->getQuery();
        $pathWithQuery = $path . ($query ? '?' . $query : '');
        $method = $request->getMethod();
        $headers = $request->getHeaders();
        $body = $request->getBody()->getContents();

        $headerLines = [];
        foreach ($headers as $name => $values) {
            foreach ($values as $value) {
                $headerLines[] = "$name: $value";
            }
        }

        $requestLine = "$method $pathWithQuery HTTP/1.1";
        $requestHeaders = "$requestLine\r\n" . implode("\r\n", $headerLines) . "\r\n\r\n";
        $requestBody = $body;

        $this->logger && $this->logger->log(LogLevel::DEBUG, "Sending request to $host:$port");

        $contextOptions = [
            'ssl' => [
                'verify_peer' => true,
                'verify_peer_name' => true,
            ],
        ];

        $context = stream_context_create($contextOptions);
        $socket = fsockopen($scheme === 'https' ? 'ssl://' . $host : $host, $port, $errno, $errstr, 30, $context);

        if (!$socket) {
            $this->logger && $this->logger->log(LogLevel::ERROR, "Failed to connect to $host:$port - $errstr ($errno)");
            throw new \RuntimeException("Failed to connect to $host:$port - $errstr ($errno)");
        }

        fwrite($socket, $requestHeaders . $requestBody);

        $responseHeaders = '';
        $responseBody = '';
        $inHeaders = true;

        while (!feof($socket)) {
            $line = fgets($socket);
            if ($inHeaders && $line === "\r\n") {
                $inHeaders = false;
            } elseif ($inHeaders) {
                $responseHeaders .= $line;
            } else {
                $responseBody .= $line;
            }
        }

        fclose($socket);

        $this->logger && $this->logger->log(LogLevel::DEBUG, "Received response from $host:$port");

        $response = $this->createResponse($responseHeaders, $responseBody);

        return $response;
    }

    private function createResponse($headers, $body): ResponseInterface
    {
        $headerLines = explode("\r\n", trim($headers));
        $statusLine = array_shift($headerLines);
        list($protocolVersion, $statusCode, $reasonPhrase) = explode(' ', $statusLine, 3);

        $response = new \GuzzleHttp\Psr7\Response($statusCode, [], $body, $protocolVersion, $reasonPhrase);

        foreach ($headerLines as $headerLine) {
            list($name, $value) = explode(':', $headerLine, 2);
            $response = $response->withAddedHeader(trim($name), trim($value));
        }

        return $response;
    }
}
