require __DIR__ . '/../tests/config.php';

class RabbitMQConsumer
{
    protected $msgCount = 0;
    protected $startTime = null;
    protected $conn;
    protected $ch;
    protected $exchange = 'bench_exchange';
    protected $queue = 'bench_queue';
    protected $consumer_tag = '';

    public function __construct()
    {
        $this->conn = new AMQPStreamConnection(HOST, PORT, USER, PASS, VHOST);
        $this->ch = $this->conn->channel();
        $this->ch->queue_declare($this->queue, false, false, false, false);
        $this->ch->exchange_declare($this->exchange, 'direct', false, false, false);
        $this->ch->queue_bind($this->queue, $this->exchange);
    }

    public function process_message($msg)
    {
        if ($this->startTime === null) {
            $this->startTime = microtime(true);
        }

        if ($msg->body == 'quit') {
            echo sprintf("Pid: %s, Count: %s, Time: %.4f\n", getmypid(), $this->msgCount, microtime(true) - $this->startTime);
            $this->shutdown();
            die;
        }
        $this->msgCount++;
    }

    public function startConsuming()
    {
        $this->ch->basic_consume($this->queue, '', false, true, false, false, array($this, 'process_message'));
        while ($this->ch->is_consuming()) {
            $this->ch->wait();
        }
    }

    protected function shutdown()
    {
        $this->ch->close();
        $this->conn->close();
    }
