            return $response === 'OK';
        };
    }

    private function getErrorHandler()
    {
        return function ($response) {
            throw new Exception("Redis error: {$response}");
        };
    }

    private function createResource()
    {
        $socket = stream_socket_client(
            "tcp://{$this->parameters->getHost()}:{$this->parameters->getPort()}",
            $errno,
            $errstr,
            $this->parameters->getTimeout()
        );

        if (!$socket) {
            throw new Exception("Connection error: {$errstr} ({$errno})");
        }

        stream_set_timeout($socket, $this->parameters->getTimeout());
        return $socket;
    }

    private function connectWithTimeout($socket, $address, ParametersInterface $parameters)
    {
        $timeout = $parameters->getTimeout();
        stream_set_timeout($socket, $timeout);
        if (!stream_socket_connect($socket, $address, $parameters->getPort(), $timeout)) {
            throw new Exception("Connection to Redis server timed out");
        }
    }

    public function write($buffer)
    {
        $socket = $this->createResource();
        $written = fwrite($socket, $buffer);
        if ($written === false || $written < strlen($buffer)) {
            throw new Exception("Failed to write to socket");
        }
        fclose($socket);
    }

    public function read()
    {
        $socket = $this->createResource();
        $response = '';
        while (!feof($socket)) {
            $chunk = fgets($socket, 4096);
            if ($chunk === false) {
                throw new Exception("Failed to read from socket");
            }
            $response .= $chunk;
            $reply = phpiredis_reader_feed($this->reader, $chunk);
            if ($reply === PHPIREDIS_REPLY_ERROR) {
                call_user_func($this->getErrorHandler(), phpiredis_reader_get_error($this->reader));
            }
            if ($reply === PHPIREDIS_REPLY_STATUS) {
                return call_user_func($this->getStatusHandler(), phpiredis_reader_get_status($this->reader));
            }
            if ($reply === PHPIREDIS_REPLY_INLINE) {
                return phpiredis_reader_get_INLINE($this->reader);
            }
        }
        fclose($socket);
        return phpiredis_reader_get Bulk($this->reader);
    }

    public function writeRequest(CommandInterface $command)
    {
        $buffer = $command->getFormattedCommand();
        $this->write($buffer);
    }
