class UdpScraper {
    private $currentConnectionId;
    private $transactionId;
    private $socket;
    private $timeout = 5;

    public function scrape(Tracker $tracker, array $hashes): array {
        $results = [];
        $chunks = array_chunk($hashes, 74);
        foreach ($chunks as $chunk) {
            $results = array_merge($results, $this->scrapeChunk($tracker, $chunk));
        }
        return $results;
    }

    private function scrapeChunk(Tracker $tracker, array $hashes): array {
        $this->initSocket();
        $this->connect($tracker->getDomain(), $tracker->getPort());
        $this->startTransaction();
        $result = $this->scrapeHashes($hashes);
        $this->closeSocket();
        return $result;
    }

    private function initSocket(): resource {
        $this->socket = socket_create(AF_INET, SOCK_DGRAM, SOL_UDP);
        socket_set_option($this->socket, SOL_SOCKET, SO_RCVTIMEO, ['sec' => $this->timeout, 'usec' => 0]);
        return $this->socket;
    }

    private function connect(string $domain, int $port): void {
        socket_connect($this->socket, $domain, $port);
    }

    private function generateTransactionId(): void {
        $this->transactionId = rand(0, 65535);
    }

    private function startTransaction(): void {
        $this->generateTransactionId();
        $connectionRequest = pack('Nnn', 0x417, 0x2710, $this->transactionId);
        $response = $this->request($connectionRequest, 16);
        $unpackResponse = unpack('Naction/NtransactionId/NconnectionId', $response);
        if ($unpackResponse['action'] === 0) {
            $this->currentConnectionId = $unpackResponse['connectionId'];
        } else {
            $this->checkError($response);
        }
    }

    private function scrapeHashes(array $hashes): array {
        $packet = pack('Nnn', $this->currentConnectionId, 2, $this->transactionId);
        foreach ($hashes as $hash) {
            $packet .= pack('H*', $hash);
        }
        $response = $this->request($packet, 8 + count($hashes) * 12);
        $results = [];
        $unpackResponse = unpack('Naction/NtransactionId', substr($response, 0, 8));
        if ($unpackResponse['action'] === 2) {
            $offset = 8;
            foreach ($hashes as $hash) {
                $result = unpack('Nseeders/Ncompleted/Nleechers', substr($response, $offset, 12));
                $results[$hash] = $result;
                $offset += 12;
            }
        } else {
            $this->checkError($response);
        }
        return $results;
    }

    private function request(string $packet, int $readLength): string {
        socket_send($this->socket, $packet, strlen($packet), 0);
        socket_recv($this->socket, $response, $readLength, 0);
        return $response;
    }

    private function closeSocket(): void {
        socket_close($this->socket);
        $this->socket = null;
        $this->currentConnectionId = null;
    }

    private function checkError(string $response): void {
        $unpackResponse = unpack('Naction/NtransactionId', $response);
        if ($unpackResponse['action'] === 3) {
            throw new Exception('Tracker error: ' . $unpackResponse['transactionId']);
        }
    }
}

