function generateImageFromDot($dot_script, $type) {
    $descriptorspec = array(
        0 => array("pipe", "r"),  // stdin
        1 => array("pipe", "w"),  // stdout
        2 => array("pipe", "w")   // stderr
    );

    $process = proc_open("dot -T$type", $descriptorspec, $pipes);

    if (is_resource($process)) {
        fwrite($pipes[0], $dot_script);
        fclose($pipes[0]);

        $image_data = stream_get_contents($pipes[1]);
        fclose($pipes[1]);

        $error_message = stream_get_contents($pipes[2]);
        fclose($pipes[2]);

        $return_value = proc_close($process);

        if ($return_value !== 0) {
            fwrite(STDERR, "Error generating image: " . $error_message);
            exit(1);
        }

        return $image_data;
    } else {
        fwrite(STDERR, "Failed to start the dot process");
        exit(1);
    }
}
