class DefaultPhpProcess {
    private $tempFile = null;
    private $stdin = null;
    private $env = null;
    private $timeout = null;

    public function runJob(string $job, array $settings = []): array {
        $this->stdin = $job;
        $this->env = $settings['env'] ?? null;
        $this->timeout = $settings['timeout'] ?? null;
        return $this->runProcess($job, $settings);
    }

    private function getHandles(): array {
        return [
            0 => ['pipe', 'r'],
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w']
        ];
    }

    private function runProcess(string $job, array $settings): array {
        $cmd = $settings['cmd'] ?? 'php';
        $descriptorSpec = $this->getHandles();
        $process = proc_open($cmd, $descriptorSpec, $pipes, null, $this->env);

        if (is_resource($process)) {
            $this->process($pipes[0], $job);
            fclose($pipes[0]);

            $stdout = stream_get_contents($pipes[1]);
            $stderr = stream_get_contents($pipes[2]);

            fclose($pipes[1]);
            fclose($pipes[2]);

            $status = proc_close($process);

            return ['stdout' => $stdout, 'stderr' => $stderr, 'status' => $status];
        }

        return ['stdout' => '', 'stderr' => 'Failed to start process', 'status' => -1];
    }

