        if ($this->useTemporaryFile()) {
            $this->tempFile = tempnam(sys_get_temp_dir(), 'input');
            file_put_contents($this->tempFile, $job);
            fwrite($pipe, $this->tempFile);
        } else {
            fwrite($pipe, $job);
        }
