<?php

abstract class AbstractPhpProcess {
    protected $tempFile;
    protected $stdin;
    protected $env;
    protected $timeout;

    public function __construct($stdin = null, $env = [], $timeout = 60) {
        $this->tempFile = tempnam(sys_get_temp_dir(), 'phpjob');
        $this->stdin = $stdin;
        $this->env = $env;
        $this->timeout = $timeout;
    }

    abstract public function runJob($job, $settings = []);
    abstract protected function getHandles();
    abstract protected function runProcess($job, $settings);
    abstract protected function process($pipe, $job);
    abstract protected function cleanup();
    abstract protected function useTemporaryFile();
}

class DefaultPhpProcess extends AbstractPhpProcess {
    public function runJob($job, $settings = []) {
        return $this->runProcess($job, $settings);
    }

    protected function getHandles() {
        return [];
    }

    protected function runProcess($job, $settings) {
        $descriptors = [
            0 => ['pipe', 'r'],
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w']
        ];

        $process = proc_open('php', $descriptors, $pipes, null, $this->env);

        if (is_resource($process)) {
            $this->process($pipes[0], $job);
            fclose($pipes[0]);

            $output = stream_get_contents($pipes[1]);
            $error = stream_get_contents($pipes[2]);

            fclose($pipes[1]);
            fclose($pipes[2]);

            $status = proc_close($process);

            return ['output' => $output, 'error' => $error, 'status' => $status];
        }

        return ['output' => '', 'error' => 'Failed to start process', 'status' => -1];
    }

    protected function process($pipe, $job) {
