<?php

class Wechatpay {
    private $sslConfig = [
        'verify_peer' => true,
        'verify_peer_name' => true,
        'allow_self_signed' => false,
        'cafile' => '/path/to/cacert.pem'
    ];

    public function http_get($url) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, $this->sslConfig['verify_peer']);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, $this->sslConfig['verify_peer_name'] ? 2 : 0);
        curl_setopt($ch, CURLOPT_CAINFO, $this->sslConfig['cafile']);

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        return $httpCode == 200 ? $response : false;
    }

    public function http_post($url, $param, $post_file = false) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, $this->sslConfig['verify_peer']);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, $this->sslConfig['verify_peer_name'] ? 2 : 0);
        curl_setopt($ch, CURLOPT_CAINFO, $this->sslConfig['cafile']);

        if ($post_file) {
            $file = new CURLFile($param['file']);
            curl_setopt($ch, CURLOPT_POSTFIELDS, ['file' => $file]);
        } else {
            curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($param));
        }

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        return $httpCode == 200 ? $response : false;
    }
}

