<?php

class Wechatpay {
    private $sslConfig = [];

    public function __construct($sslConfig = []) {
        $this->sslConfig = $sslConfig;
    }

    private function makeRequest($url, $method, $data = null, $post_file = false) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2);
        if (!empty($this->sslConfig)) {
            curl_setopt($ch, CURLOPT_SSLCERT, $this->sslConfig['cert']);
            curl_setopt($ch, CURLOPT_SSLCERTPASSWD, $this->sslConfig['cert_password']);
            curl_setopt($ch, CURLOPT_SSLKEY, $this->sslConfig['key']);
            curl_setopt($ch, CURLOPT_SSLKEYPASSWD, $this->sslConfig['key_password']);
        }

        if ($method === 'POST') {
            curl_setopt($ch, CURLOPT_POST, true);
            if ($post_file) {
                curl_setopt($ch, CURLOPT_POSTFIELDS, $data);
            } else {
                curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data));
            }
        }

        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);

        return ($httpCode === 200) ? $response : false;
    }

    public function http_get($url) {
        return $this->makeRequest($url, 'GET');
    }

    public function http_post($url, $param, $post_file = false) {
        return $this->makeRequest($url, 'POST', $param, $post_file);
    }
}

