    public function imageUrl(
        int $width,
        int $height,
        string $category = null,
        bool $randomize = true,
        string $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string {
        $url = self::BASE_URL . "/{$width}x{$height}";
        if ($category && in_array($category, $this->categories)) {
            $url .= "/{$category}";
        }
        if ($word) {
            $url .= "/{$word}";
        }
        if ($gray) {
            $url .= "/gray";
        }
        if ($randomize) {
            $url .= "?random=" . rand(1, 1000);
        }
        $url .= ".{$format}";
        return $url;
    }

    private function generateFilename(
        int $width,
        int $height,
        string $category = null,
        string $word = null,
        bool $gray = false,
        string $format = 'png'
    ): string {
        $parts = [$width, $height];
        if ($category) {
            $parts[] = $category;
        }
        if ($word) {
            $parts[] = $word;
        }
        if ($gray) {
            $parts[] = 'gray';
        }
        return implode('_', $parts) . '.' . $format;
    }
