        $self = curl_getinfo($h, CURLINFO_PRIVATE);
        $maxSize = 1048576; // 1MB size limit

        if ($self->curlDataWritten + strlen($d) > $maxSize) {
            return 0;
        }

        $written = fwrite($self->curlFH, $d);
        $self->curlDataWritten += $written;
        return $written;
    }

    public function getURL($url, $tempfile) {
        $this->lastURLError = false;

        if (function_exists('curl_init')) {
            $ch = curl_init($url);
            $this->curlFH = fopen($tempfile, 'w');
            curl_setopt($ch, CURLOPT_WRITEFUNCTION, ['timthumb', 'curlWrite']);
